One.onLoad((() => class {
    static initValidation() {
        One.helpers("jq-validation"), jQuery(".js-validation-lock").validate({
            rules: {
                "password": {
                    required: !0,
                    minlength: 3
                }
            },
            messages: {
                "password": {
                    required: "Por favor ingrese su contraseña válida"
                }
            }
        })
    }
    static init() {
        this.initValidation()
    }
}.init()));