One.onLoad((() => class {
    static initValidation() {
        One.helpers("jq-validation"), jQuery(".js-validation-signin").validate({
            rules: {
                "email": {
                    required: !0,
                    minlength: 3
                },
                "password": {
                    required: !0,
                    minlength: 4
                }
            },
            messages: {
                "email": {
                    required: "Ingresar el nombre de usuario o correo",
                    minlength: "Su nombre de usuario debe constar de al menos 3 caracteres"
                },
                "password": {
                    required: "Ingresar contraseña",
                    minlength: "Tu contraseña debe tener al menos 5 caracteres"
                }
            }
        })
    }
    static init() {
        this.initValidation()
    }
}.init()));